import React, {Component} from 'react';
import './App.css';
import Navigation from './components/navigation/navigation';
import Logo from './components/logo/logo';
import ImageLink from './components/imagelink/imagelink';
import Rank from './components/rank/rank';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import FaceRecog from './components/facerecog/facerecog';

const app = new Clarifai.App({
  apiKey: 'c8d02018a78441f49fa119231458234b'
 });

const particlesOptions = {
    particles: {
      number: {
      value: 30,
      density: {
        enable: true,
        value_area: 250
        }
      }
    }
  }

class App extends Component{
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
    }
  }
  onInputChange = (event) => {
    console.log(event.target.value);
  }
  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input})
    app.models.predict( Clarifai.COLOR_MODEL, "https://samples.clarifai.com/face-det.jpg").then(
    function(response) {
      // do something with response
      console.log(response)
    },
    function(err) {
      // there was an error
    }
  );
  }
  render() {

  return (
    <div className="App">
       <Particles className='particles'
              params={particlesOptions}
            />
    <Navigation />
      <Logo />
      <ImageLink onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit} />
      <Rank />
      <FaceRecog imageUrl={this.state.imageUrl} />

    </div>
    );
  } 
}


export default App;
